// Swiper 라이브러리 더미 (CDN으로 대체)
// 실제 사용 시에는 https://unpkg.com/swiper@8/swiper-bundle.min.js 에서 다운로드
console.log('⚠️ Swiper 더미 파일 로드됨 - CDN 사용 권장');

// 기본 Swiper 더미 객체
window.Swiper = function(selector, options) {
    console.log('Swiper 더미 생성자:', selector, options);
    return {
        autoplay: {
            start: function() { console.log('Swiper autoplay start'); },
            stop: function() { console.log('Swiper autoplay stop'); }
        },
        update: function() { console.log('Swiper update'); }
    };
};


